mapper.update:
update ser_catalogue_detail t
t.edittime=sysdate(), t.edituserid=#{edituserid}, t.editgroupid=#{editgroupid}, t.cata_detail_name=#{cata_detail_name}, t.cata_detail_desc=#{cata_detail_desc}
where t.cata_detail_id=#{cata_detail_id}

update ser_catalogue t
t.edittime=sysdate(), t.edituserid=#{edituserid}, t.editgroupid=#{editgroupid}, t.cata_code=#{cata_code}, t.cata_name=#{cata_name}, t.cata_pingyin=#{cata_pingyin}, t.cata_desc=#{cata_desc}, t.cata_sort=#{cata_sort}, t.bus_type_id=#{bus_type_id}, t.event_type_id=#{event_type_id}, t.consumer_type_id=#{consumer_type_id}, t.power_type_id=#{power_type_id}, t.department_id=#{department_id}, t.cata_hand_time_limit=#{cata_hand_time_limit}, t.cata_promise_time_limit=#{cata_promise_time_limit}, t.cata_complaint_tel=#{cata_complaint_tel}, t.cata_is_net=#{cata_is_net}, t.cata_video_url=#{cata_video_url}, t.cata_is_charge=#{cata_is_charge}, t.cata_establish=#{cata_establish}, t.cata_hand_term=#{cata_hand_term}, t.cata_app_material=#{cata_app_material}, t.cata_process=#{cata_process}
where t.cata_id=#{cata_id}
