mapper.update:
update sys_permission t
t.permname=#{permname}, t.permcode=#{permcode}, t.permdescribe=#{permdescribe}, t.parentid=#{parentid}, permtype=#{permtype}, url=#{url}, sortnum=#{sortnum}, iconcss=#{iconcss}, enabled=#{enabled}, isblanktarget=#{isblanktarget}, updatetime=sysdate()
where t.permissionid=#{permissionid}
